import{o as e,c as s,d as n}from"./404.md.9bcdd6c7.js";const a='{"title":"Feature Types","description":"","frontmatter":{"title":"Feature Types"},"headers":[{"level":2,"title":"Content Features","slug":"content-features"},{"level":3,"title":"Single Block Features","slug":"single-block-features"},{"level":3,"title":"Ore Features","slug":"ore-features"},{"level":3,"title":"Structure Template Features","slug":"structure-template-features"},{"level":3,"title":"Growing Plant Features","slug":"growing-plant-features"},{"level":3,"title":"Tree Features","slug":"tree-features"},{"level":3,"title":"Multiface Features","slug":"multiface-features"},{"level":2,"title":"Proxy Features","slug":"proxy-features"},{"level":3,"title":"Scatter Features","slug":"scatter-features"},{"level":3,"title":"Conditional Lists","slug":"conditional-lists"},{"level":3,"title":"Aggregate Features","slug":"aggregate-features"},{"level":3,"title":"Sequence Features","slug":"sequence-features"},{"level":3,"title":"Snap-to-Surface Features","slug":"snap-to-surface-features"},{"level":3,"title":"Search Features","slug":"search-features"},{"level":3,"title":"Rect Layouts","slug":"rect-layouts"},{"level":3,"title":"Scan Surface Features","slug":"scan-surface-features"},{"level":3,"title":"Weighted Random Features","slug":"weighted-random-features"},{"level":2,"title":"Scene Features","slug":"scene-features"},{"level":3,"title":"Geode Features","slug":"geode-features"},{"level":3,"title":"Beards and Shavers","slug":"beards-and-shavers"},{"level":3,"title":"Vegetation Patches","slug":"vegetation-patches"},{"level":2,"title":"Carver Features","slug":"carver-features"},{"level":3,"title":"Cave Carver Features","slug":"cave-carver-features"},{"level":3,"title":"Hell Cave Carver Features","slug":"hell-cave-carver-features"},{"level":3,"title":"Underwater Cave Carver Features","slug":"underwater-cave-carver-features"}],"relativePath":"world-generation/feature-types.md","lastUpdated":1624745135221}',t={},r=n('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Some links designed to reference external documents do not function, and will be updated at a later time to point to the correct resource.</p><p>Screenshots and other resources may be provided for many feature types given here at a later time.</p></div><h2 id="content-features"><a class="header-anchor" href="#content-features" aria-hidden="true">#</a> Content Features</h2><h3 id="single-block-features"><a class="header-anchor" href="#single-block-features" aria-hidden="true">#</a> Single Block Features</h3><h3 id="ore-features"><a class="header-anchor" href="#ore-features" aria-hidden="true">#</a> Ore Features</h3><h3 id="structure-template-features"><a class="header-anchor" href="#structure-template-features" aria-hidden="true">#</a> Structure Template Features</h3><h3 id="growing-plant-features"><a class="header-anchor" href="#growing-plant-features" aria-hidden="true">#</a> Growing Plant Features</h3><h3 id="tree-features"><a class="header-anchor" href="#tree-features" aria-hidden="true">#</a> Tree Features</h3><h3 id="multiface-features"><a class="header-anchor" href="#multiface-features" aria-hidden="true">#</a> Multiface Features</h3><h2 id="proxy-features"><a class="header-anchor" href="#proxy-features" aria-hidden="true">#</a> Proxy Features</h2><p>Proxy features group, arrange, or gate features, including other proxy features. Proxy features themselves are incapable of having a direct effect on world generation.</p><p>All proxy features must therefore point to one or multiple <strong>target features</strong>: the features that are placed, rearranged, or selected by proxy features. Target features are represented as string references to the identifier of the intended feature.</p><h3 id="scatter-features"><a class="header-anchor" href="#scatter-features" aria-hidden="true">#</a> Scatter Features</h3><div class="language-json line-numbers-mode"><pre><code><span class="token punctuation">{</span>\n\t<span class="token property">&quot;format_version&quot;</span><span class="token operator">:</span> <span class="token string">&quot;1.13.0&quot;</span><span class="token punctuation">,</span>\n\n\t<span class="token property">&quot;minecraft:scatter_feature&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t\t<span class="token property">&quot;description&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t\t\t<span class="token property">&quot;identifier&quot;</span><span class="token operator">:</span> <span class="token string">&quot;arboreal:flower_patch&quot;</span>\n\t\t<span class="token punctuation">}</span><span class="token punctuation">,</span>\n\n\t\t<span class="token property">&quot;places_feature&quot;</span><span class="token operator">:</span> <span class="token string">&quot;arboreal:flowers&quot;</span><span class="token punctuation">,</span>\n\n\t\t<span class="token property">&quot;scatter_chance&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t\t\t<span class="token property">&quot;numerator&quot;</span><span class="token operator">:</span> <span class="token number">2</span><span class="token punctuation">,</span>\n\t\t\t<span class="token property">&quot;denominator&quot;</span><span class="token operator">:</span> <span class="token number">3</span>\n\t\t<span class="token punctuation">}</span><span class="token punctuation">,</span>\n\t\t<span class="token property">&quot;iterations&quot;</span><span class="token operator">:</span> <span class="token string">&quot;v.flower_patch.size = math.random_integer(6, 14); return math.random_integer(math.pow(v.flower_patch.size, 2) / 4, math.pow(v.flower_patch.size, 2) / 3);&quot;</span><span class="token punctuation">,</span>\n\n\t\t<span class="token property">&quot;project_input_to_floor&quot;</span><span class="token operator">:</span> <span class="token boolean">true</span><span class="token punctuation">,</span>\n\t\t<span class="token property">&quot;x&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t\t\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;gaussian&quot;</span><span class="token punctuation">,</span>\n\t\t\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token string">&quot;v.flower_patch.size&quot;</span><span class="token punctuation">]</span>\n\t\t<span class="token punctuation">}</span><span class="token punctuation">,</span>\n\t\t<span class="token property">&quot;z&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t\t\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;gaussian&quot;</span><span class="token punctuation">,</span>\n\t\t\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token string">&quot;v.flower_patch.size&quot;</span><span class="token punctuation">]</span>\n\t\t<span class="token punctuation">}</span><span class="token punctuation">,</span>\n\t\t<span class="token property">&quot;y&quot;</span><span class="token operator">:</span> <span class="token number">0</span>\n\t<span class="token punctuation">}</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br></div></div><p><strong>Scatter features</strong> are the most flexible and useful feature type available. Scatter features can:</p><ul><li>Distribute or reposition a feature any number of times within a <a href="#">chunk’s feature domain</a></li><li>Act as a gate to conditionally enable a feature to be placed</li><li>Execute MoLang within the current <a href="#">feature context</a></li></ul><p>Scatter features attempt to place a <a href="#proxy-features">target feature</a> with each iteration:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;places_feature&quot;</span><span class="token operator">:</span> <span class="token string">&quot;lostlands:shimmerfields_spire&quot;</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>If, where, and how an instance of the target is placed depend on the <a href="#generation-potential">generation potential</a>, <a href="#distribution">distribution</a>, and the <a href="#evaluation-order">evaluation order</a></p><h4 id="generation-potential"><a class="header-anchor" href="#generation-potential" aria-hidden="true">#</a> Generation Potential</h4><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;scatter_chance&quot;</span><span class="token operator">:</span> <span class="token number">25</span><span class="token punctuation">,</span>\n<span class="token property">&quot;iterations&quot;</span><span class="token operator">:</span> <span class="token number">12</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>A scatter feature will determine placement attempts of its target using the <code>&quot;scatter_chance&quot;</code> and <code>&quot;iterations&quot;</code> properties.</p><p><strong>Scatter chance</strong> represents the potential for the scatter feature to succeed. It can be represented as…</p><p>A numeric literal:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;scatter_chance&quot;</span><span class="token operator">:</span> <span class="token number">12.5</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>The numeric literal form is considered against 100, not 1. A scatter chance of <code>50</code>, therefore, has half a chance of success.</p></div><p>A MoLang expression:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;scatter_chance&quot;</span><span class="token operator">:</span> <span class="token string">&quot;1 / 8&quot;</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>A fraction object:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;scatter_chance&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;numerator&quot;</span><span class="token operator">:</span> <span class="token number">1</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;denominator&quot;</span><span class="token operator">:</span> <span class="token number">8</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>All 3 examples have a 12.5% chance for success. Use whichever form feels most appropriate for your case. If scatter chance is omitted, it defaults to a 100% chance for the scatter feature to attempt to place its target.</p><p><strong>Iterations</strong> are the number of attempts a scatter feature will try to place its target. If an instance of a scatter feature would succeed (in other words, if its scatter chance check were successful), <em>all</em> of the iterations given by <code>&quot;iterations&quot;</code> will be attempted. Iterations may be represented as integer literals or MoLang expressions. Unlike scatter chance, iterations are required.</p><h4 id="distribution"><a class="header-anchor" href="#distribution" aria-hidden="true">#</a> Distribution</h4><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;x&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;fixed_grid&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">15</span><span class="token punctuation">]</span>\n<span class="token punctuation">}</span><span class="token punctuation">,</span>\n<span class="token property">&quot;z&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;fixed_grid&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">15</span><span class="token punctuation">]</span>\n<span class="token punctuation">}</span><span class="token punctuation">,</span>\n<span class="token property">&quot;y&quot;</span><span class="token operator">:</span> <span class="token number">0</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><p>Distribution is predominantly handled using <strong>coordinate properties</strong>: <code>&quot;x&quot;</code>, <code>&quot;z&quot;</code>, and <code>&quot;y&quot;</code>. All these properties may be represented using…</p><p>An integer literal:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;x&quot;</span><span class="token operator">:</span> <span class="token number">0</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>A MoLang Expression:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;x&quot;</span><span class="token operator">:</span> <span class="token string">&quot;math.random_integer(0, v.surface_grass.spread - 1)&quot;</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>Or a number of object forms for conveniently distributing a coordinate:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;x&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;uniform&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">16</span><span class="token punctuation">]</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>Literals and MoLang expressions are relative to the <a href="#">feature origin</a>. See <a href="#distribution-types">Distribution Types</a> for the available pre-constructed distribution systems.</p><p>Because placement of features is so often relative to the heightmap, the incoming <em>y</em>-origin for the scatter feature may be <strong>projected into the heightmap</strong>:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;project_input_to_floor&quot;</span><span class="token operator">:</span> <span class="token boolean">true</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>This means that the specified <em>y</em>-origin from the scatter feature’s parent is ignored in favor of the <em>y</em>-coordinate of the heightmap at an iteration’s <em>x</em>-<em>z</em> location (<a href="#evaluation-order">assuming the <em>y</em>-coordinate would be evaluated after the lateral coordinates</a>). The <code>&quot;y&quot;</code> property may still be given a value that will represent the offset from the heightmap.</p><div class="tip custom-block"><p class="custom-block-title">Note</p><p>Functionally, this is the same as using the MoLang expression <code>&quot;query.heightmap(v.worldx, v.worldz) + *offset*&quot;</code>.</p></div><h5 id="distribution-types"><a class="header-anchor" href="#distribution-types" aria-hidden="true">#</a> Distribution Types</h5><p>Custom distribution systems can be constructed using MoLang expressions, but scatter features come pre-equipped with a few common <strong>distribution types</strong> for convenient authoring:</p><ul><li>Uniform</li><li>Gaussian</li><li>Inverse Gaussian</li><li>Fixed grid</li><li>Jittered grid</li></ul><p>Each distribution type requires an <strong>extent</strong>, which represents the range of values on which that distribution operates, from minimum to maximum. Extents, like the basic forms of coordinate declarations, are relative to the <a href="#">feature origin</a>.</p><h6 id="uniform-distribution"><a class="header-anchor" href="#uniform-distribution" aria-hidden="true">#</a> Uniform Distribution</h6><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;z&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;uniform&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token string">&quot;v.boulder_spread.start&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;v.boulder_spread.end&quot;</span><span class="token punctuation">]</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p><strong>Uniform distribution</strong> is uniformly random distribution on a half-open interval between two values. It is known as “uniform” because every value within the range has an equal chance of being selected and “half-open” because the extent minimum is a member of the range, while the extent maximum is not:</p><p><i>minimum extent</i> &lt;= <i>x</i> &lt; <i>maximum extent</i></p><p>Therefore, if an extent of <code>[0, 16]</code> were given for a uniform distribution, blocks may be placed in a range of size 16: from 0 to 15. The 1st possible position starts at 0 while the 15th possible position ends at 16, matching the extent.</p><h6 id="gaussian-distributions"><a class="header-anchor" href="#gaussian-distributions" aria-hidden="true">#</a> Gaussian Distributions</h6><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;y&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;gaussian&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token string">&quot;2 * v.vine_cluster.radius&quot;</span><span class="token punctuation">]</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p><strong>Gaussian distribution</strong> (<code>&quot;gaussian&quot;</code>) and its <strong>inverse</strong> (<code>&quot;inverse_gaussian&quot;</code>) are useful for grouping features together: toward or away from the center of the extent respectively. Gaussian distribution is so extreme that values will almost never be selected away from the center with normal Gaussian distribution or toward the center with inverse Gaussian distribution. The extents for Gaussian distribution behave the as with <a href="#uniform-distribution">uniform distribution</a>.</p><h6 id="grid-distributions"><a class="header-anchor" href="#grid-distributions" aria-hidden="true">#</a> Grid Distributions</h6><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;x&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;jittered_grid&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">15</span><span class="token punctuation">]</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;step_size&quot;</span><span class="token operator">:</span> <span class="token number">2</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;grid_offset&quot;</span><span class="token operator">:</span> <span class="token number">4</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p><strong>Grid distributions</strong> are powerful systems for placing blocks either directly on (<code>&quot;fixed_grid&quot;</code>) or randomly within (<code>&quot;jittered_grid&quot;</code>) evenly spaced intervals along a coordinate. Unlike the other distribution types, the extent of grids forms an interval that includes the maximum extent:</p><p><i>minimum extent</i> &lt;= <i>x</i> &lt;= <i>maximum extent</i></p><p>Two grid distribution-only properties are available for finer control over the grids used by these systems. The interval size, which defaults to 1, can be customized with the <code>&quot;step_size&quot;</code> property. An initial offset, defaulting to 0, can also be provided via the <code>&quot;grid_offset&quot;</code> property.</p><p>If the iteration count in conjunction with the step size and offset would push a coordinate beyond the maximum extent, the coordinate will be wrapped back to the minimum extent and continue from there.</p><p>While grids are useful on a coordinate independently, their true power shows when in combination with grid distributions on other coordinates. Placements prioritize incrementing the earliest evaluated grid system before later systems; the later layouts are only considered when wrapping occurs in the previous grid system. When a placement in an earlier evaluated coordinate would wrap, the next evaluated grid-powered coordinate will be offset by the number of wraps that occurred.</p><p>As a simple example:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;iterations&quot;</span><span class="token operator">:</span> <span class="token number">21</span><span class="token punctuation">,</span>\n\n<span class="token property">&quot;x&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;fixed_grid&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">15</span><span class="token punctuation">]</span>\n<span class="token punctuation">}</span><span class="token punctuation">,</span>\n<span class="token property">&quot;z&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t<span class="token property">&quot;distribution&quot;</span><span class="token operator">:</span> <span class="token string">&quot;fixed_grid&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;extent&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">15</span><span class="token punctuation">]</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br></div></div><p>Placements will first begin along <em>x</em>: (0, 0), (1, 0), etc., until reaching the end of the extent at (15, 0). However, only 16 of the 21 iterations have occurred; 5 remain. Now, the <em>x</em>-coordinate wraps back around to 0, while the <em>z</em>-coordinate increments to 1: (0, 1).</p><p>This wrapping occurs in three-dimensions, too, so when a plane along the earliest evaluated coordinates would wrap (assuming a high enough iteration count), another plane will begin formation based on the final coordinate’s step size.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Grid extents behave in unexpected ways when 0 is not within the extent range. In particular, the extent will be projected into 0 by the closest bound. For example, the given extent <code>[-7, -2]</code> will be remapped as though it were <code>[-5, 0]</code>. <code>[13, 21]</code> would get remapped to <code>[0, 8]</code>. For this reason, it&#39;s recommended to just specify the extent as a length, such as using <code>[0, 8]</code> in the latter example, and proxying that scatter feature with another scatter feature that would position that coordinate using a literal <code>13</code>.</p><p>When using multiple grid distributions to form a surface or volume, extents for any coordinate that would extend below <code>0</code> will only work on the first pass of that coordinate. Later passes will be restricted to non-negative values.</p></div><h4 id="evaluation-order"><a class="header-anchor" href="#evaluation-order" aria-hidden="true">#</a> Evaluation Order</h4><p>When a scatter chance is included, it is evaluated before any other properties. If the check against scatter chance fails for that instance of the scatter feature, nothing downstream within that instance is evaluated. No further MoLang is interpreted; no variables within the <a href="#">feature context</a> are updated. The target feature is entirely disregarded.</p><p>Next, the iteration count is evaluated. Similarly to scatter chance, if the iteration count were not to resolve to a positive number of placement attempts, nothing further is evaluated.</p><p>Next, every iteration is attempted regardless of whether an early iteration would for some reason fail. For each iteration, each coordinate is evaluated using the same ordering across all iterations.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>The order of coordinate evaluation is <em>not</em> dependent upon the order the coordinate properties are declared in JSON.</p></div><p>By default, the coordinate ordering is <em>x</em> then <em>z</em> then <em>y</em>. This covers the majority of use-cases: if the coordinates aren’t independent, more than likely the vertical position depends on the lateral coordinates. However, scatter features may declare an atypical <strong>coordinate evaluation order</strong> for full control over coordinate dependence:</p><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;coordinate_eval_order&quot;</span><span class="token operator">:</span> <span class="token string">&quot;zyx&quot;</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>After the coordinates for an iteration have been determined, world generation moves its focus within the target: acknowledging its restrictions, attempting its placement, evaluating its MoLang, and (if possible and relevant) continuing by placing its children and their children and so forth.</p><p>When finished with the target’s feature tree, if more iterations have yet to be run from the scatter feature, focus returns to the scatter feature beginning with the first-evaluated coordinate and execution is resumed.</p><h3 id="conditional-lists"><a class="header-anchor" href="#conditional-lists" aria-hidden="true">#</a> Conditional Lists</h3><div class="language-json line-numbers-mode"><pre><code><span class="token punctuation">{</span>\n\t<span class="token property">&quot;format_version&quot;</span><span class="token operator">:</span> <span class="token string">&quot;1.13.0&quot;</span><span class="token punctuation">,</span>\n\n\t<span class="token property">&quot;minecraft:conditional_list&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t\t<span class="token property">&quot;description&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>\n\t\t\t<span class="token property">&quot;identifier&quot;</span><span class="token operator">:</span> <span class="token string">&quot;olympus:columns_selection&quot;</span>\n\t\t<span class="token punctuation">}</span><span class="token punctuation">,</span>\n\n\t\t<span class="token property">&quot;conditional_features&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span>\n\t\t\t<span class="token punctuation">{</span>\n\t\t\t\t<span class="token property">&quot;places_feature&quot;</span><span class="token operator">:</span> <span class="token string">&quot;olympus:columns_unweathered&quot;</span><span class="token punctuation">,</span>\n\t\t\t\t<span class="token property">&quot;condition&quot;</span><span class="token operator">:</span> <span class="token string">&quot;query.noise(v.originx, v.originz) &lt; 0&quot;</span>\n\t\t\t<span class="token punctuation">}</span><span class="token punctuation">,</span>\n\t\t\t<span class="token punctuation">{</span>\n\t\t\t\t<span class="token property">&quot;places_feature&quot;</span><span class="token operator">:</span> <span class="token string">&quot;olympus:columns_weathered&quot;</span><span class="token punctuation">,</span>\n\t\t\t\t<span class="token property">&quot;condition&quot;</span><span class="token operator">:</span> <span class="token string">&quot;query.noise(v.originx, v.originz) &gt;= 0&quot;</span>\n\t\t\t<span class="token punctuation">}</span>\n\t\t<span class="token punctuation">]</span><span class="token punctuation">,</span>\n\n\t\t<span class="token property">&quot;early_out_scheme&quot;</span><span class="token operator">:</span> <span class="token string">&quot;placement_success&quot;</span>\n\t<span class="token punctuation">}</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br></div></div><p><strong>Conditional lists</strong> pick a single feature from a collection based on conditions; they are akin to if-else if blocks in programming languages. Once a condition has been evaluated as successful (as determined via <a href="#success-determination">success determination</a>, the conditional list will select <em>only that one feature</em> for placement.</p><div class="tip custom-block"><p class="custom-block-title">Note</p><p>Instead, if <em>every</em> success should place a feature in the same location, use an <a href="#aggregate-features">aggregate feature</a> pointing to <a href="#scatter-features">scatter features</a> that proxy the target features.</p></div><h4 id="conditions-list"><a class="header-anchor" href="#conditions-list" aria-hidden="true">#</a> Conditions List</h4><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;conditional_features&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span>\n\t<span class="token punctuation">{</span>\n\t\t<span class="token property">&quot;places_feature&quot;</span><span class="token operator">:</span> <span class="token string">&quot;summer_fun:beachadjustment_water&quot;</span><span class="token punctuation">,</span>\n\t\t<span class="token property">&quot;condition&quot;</span><span class="token operator">:</span> <span class="token string">&quot;query.heightmap(v.originx, v.originz) &lt; 63 &amp;&amp; query.noise(v.originx, v.originz) &lt; 0&quot;</span>\n\t<span class="token punctuation">}</span><span class="token punctuation">,</span>\n\t<span class="token punctuation">{</span>\n\t\t<span class="token property">&quot;places_feature&quot;</span><span class="token operator">:</span> <span class="token string">&quot;summer_fun:beachadjustment_coral&quot;</span><span class="token punctuation">,</span>\n\t\t<span class="token property">&quot;condition&quot;</span><span class="token operator">:</span> <span class="token string">&quot;query.heightmap(v.originx, v.originz) &lt; 63 &amp;&amp; query.noise(v.originx, v.originz) &gt;= 0&quot;</span>\n\t<span class="token punctuation">}</span><span class="token punctuation">,</span>\n\t<span class="token punctuation">{</span>\n\t\t<span class="token property">&quot;places_feature&quot;</span><span class="token operator">:</span> <span class="token string">&quot;summer_fun:beachadjustment_air&quot;</span><span class="token punctuation">,</span>\n\t\t<span class="token property">&quot;condition&quot;</span><span class="token operator">:</span> <span class="token string">&quot;query.noise(v.originx, v.originz) &gt;= 63&quot;</span>\n\t<span class="token punctuation">}</span>\n<span class="token punctuation">]</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br></div></div><p>The <strong>conditions list</strong>, <code>&quot;conditional_features&quot;</code>, is an ordered array comprised of <strong>feature entries</strong> objects. Feature entries bind <a href="#proxy-features">target features</a> to their <strong>conditions</strong>:</p><div class="language-json line-numbers-mode"><pre><code><span class="token punctuation">{</span>\n\t<span class="token property">&quot;places_feature&quot;</span><span class="token operator">:</span> <span class="token string">&quot;verona:evergreen_trees_stumps&quot;</span><span class="token punctuation">,</span>\n\t<span class="token property">&quot;condition&quot;</span><span class="token operator">:</span> <span class="token string">&quot;v.evergreen_forest.type == v.evergreen_forest.types.lumberjack_ruined&quot;</span>\n<span class="token punctuation">}</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>The condition of each feature entry is evaluated by entry order in the conditions list. Once a feature entry <a href="#success-determination">would succeed</a>, no later-listed conditions will be evaluated.</p><h4 id="success-determination"><a class="header-anchor" href="#success-determination" aria-hidden="true">#</a> Success Determination</h4><div class="language-json line-numbers-mode"><pre><code><span class="token property">&quot;early_out_scheme&quot;</span><span class="token operator">:</span> <span class="token string">&quot;placement_success&quot;</span>\n</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>Condition success is considered in light of the optional <strong>early out scheme</strong>. Two mechanisms are provided for controlling if a feature entry would succeed. <code>&quot;condition_success&quot;</code> — the default if no <code>&quot;early_out_scheme&quot;</code> is provided — considers a success to occur when a condition evaluates to true. <code>&quot;placement_success&quot;</code> goes further: a condition must evaluate to true, and its target feature’s placement must succeed.</p><h3 id="aggregate-features"><a class="header-anchor" href="#aggregate-features" aria-hidden="true">#</a> Aggregate Features</h3><h3 id="sequence-features"><a class="header-anchor" href="#sequence-features" aria-hidden="true">#</a> Sequence Features</h3><h3 id="snap-to-surface-features"><a class="header-anchor" href="#snap-to-surface-features" aria-hidden="true">#</a> Snap-to-Surface Features</h3><h3 id="search-features"><a class="header-anchor" href="#search-features" aria-hidden="true">#</a> Search Features</h3><h3 id="rect-layouts"><a class="header-anchor" href="#rect-layouts" aria-hidden="true">#</a> Rect Layouts</h3><h3 id="scan-surface-features"><a class="header-anchor" href="#scan-surface-features" aria-hidden="true">#</a> Scan Surface Features</h3><h3 id="weighted-random-features"><a class="header-anchor" href="#weighted-random-features" aria-hidden="true">#</a> Weighted Random Features</h3><h2 id="scene-features"><a class="header-anchor" href="#scene-features" aria-hidden="true">#</a> Scene Features</h2><h3 id="geode-features"><a class="header-anchor" href="#geode-features" aria-hidden="true">#</a> Geode Features</h3><h3 id="beards-and-shavers"><a class="header-anchor" href="#beards-and-shavers" aria-hidden="true">#</a> Beards and Shavers</h3><h3 id="vegetation-patches"><a class="header-anchor" href="#vegetation-patches" aria-hidden="true">#</a> Vegetation Patches</h3><h2 id="carver-features"><a class="header-anchor" href="#carver-features" aria-hidden="true">#</a> Carver Features</h2><h3 id="cave-carver-features"><a class="header-anchor" href="#cave-carver-features" aria-hidden="true">#</a> Cave Carver Features</h3><h3 id="hell-cave-carver-features"><a class="header-anchor" href="#hell-cave-carver-features" aria-hidden="true">#</a> Hell Cave Carver Features</h3><h3 id="underwater-cave-carver-features"><a class="header-anchor" href="#underwater-cave-carver-features" aria-hidden="true">#</a> Underwater Cave Carver Features</h3>',105);t.render=function(n,a,t,o,p,i){return e(),s("div",null,[r])};export default t;export{a as __pageData};
